#ifndef SMITE_ACTIONS_COMMON
#define SMITE_ACTIONS_COMMON


#define DIVZERO(x)                              \
    if (x == 0)                                 \
        RAISE(-4);

FILE *trace_fp = NULL; // FILE * of trace file, if used
static void trace(int type, smite_WORD opcode) {
    if (trace_fp)
        fprintf(trace_fp, "%d %08x\n", type, (smite_UWORD)opcode);
}


#endif
