#ifndef SMITE_ACTIONS_COMMON
#define SMITE_ACTIONS_COMMON


#include <string.h>


#define DIVZERO(x)                              \
    if (x == 0)                                 \
        RAISE(-8);

static void trace(smite_state *S, int type, smite_WORD opcode) {
    if (S->trace_fp)
        fprintf(S->trace_fp, "%d %"PRI_XWORD"\n", type, (smite_UWORD)opcode);
}


#endif
